import React from "react";
import { newContextComponents } from "@drizzle/react-components";
import logo from "./logo.png";

//const { AccountData, ContractData, ContractForm } = newContextComponents;
const { AccountData, ContractData } = newContextComponents;

export default ({ drizzle, drizzleState }) => {
  // destructure drizzle and drizzleState from props
  return (
    <div className="App">
      <div>
        <img src={logo} alt="drizzle-logo" />
        <h1>Drizzle Examples</h1>
        <p>
          Exemplo de como usar o drizzle!
        </p>
        <p>
          Baseado no box: <a href="https://www.trufflesuite.com/boxes/drizzle">https://www.trufflesuite.com/boxes/drizzle</a>
        </p>
      </div>

      <div className="section">
        <h2>Minha Carteira Ethereum</h2>
        <AccountData
          drizzle={drizzle}
          drizzleState={drizzleState}
          accountIndex={0}
          units="ether"
          precision={3}
        />
      </div>

      <div className="section">
        <h2>Saldo da Conta no Banco</h2>
        <p>
          Exemplo de como pegar um atributo do contrato com a minha pr√≥pria chave.
        </p>
        <p>
          <strong>Saldo Atual: </strong>
          <ContractData
            drizzle={drizzle}
            drizzleState={drizzleState}
            contract="Bank"
            method="clients"
            methodArgs={[drizzleState.accounts[0]]}
          />
        </p>

      </div>
      <div className="section">
        <h2>TutorialToken</h2>
        <p>
          Here we have a form with custom, friendly labels. Also note the token
          symbol will not display a loading indicator. We've suppressed it with
          the <code>hideIndicator</code> prop because we know this variable is
          constant.
        </p>
      </div>
      <h3>Send Tokens</h3>
      <ContractForm
        drizzle={drizzle}
        contract="Bank"
        method="deposit"

      />

    </div>
  );
};
